    string name = "";
    int age = 0;
    //INSERT YOUR CODE HERE AS NEEDED
    int retirementYear = 0;
    int yearsToRetirement = 0;


    cout << "Enter your name: ";
    //INSERT YOUR CODE HERE
    cin >> name;

    cout << "Enter your age: ";
    //INSERT YOUR CODE HERE
    cin >> age;

    //INSERT YOUR CODE HERE
     yearsToRetirement = AGE_OF_RETIREMENT - age;
     retirementYear = CURRENT_YEAR + yearsToRetirement;

     cout << name << ", here is some information about your retirement:" << endl;
     cout << "Retirement year: " << retirementYear << endl;
     cout << "Number of years to retirement: " << yearsToRetirement << endl;
    return 0;
}

