#include <iostream>
int main( void )
{
    int num1 = 0, num2 = 0;
    std::cout << "Enter a number: ";
    std::cin >> num1;
    std::cout << "Enter another number: ";
    std::cin >> num2;

    /***** ENTER CODE BELOW*****/

    int sum = num1 + num2;
    int dif = num1 - num2;
    int prod = num1 * num2;
    int quot = num1 / num2;
    int rem = num1 % num2;


    std::cout << num1 << " + " << num2 << " = " << sum << std::endl;
    std::cout << num1 << " - " << num2 << " = " << dif << std::endl;
    std::cout << num1 << " * " << num2 << " = " << prod << std::endl;
    std::cout << num1 << " / " << num2 << " = " << quot << std::endl;
    std::cout << num1 << " % " << num2 << " = " << rem << std::endl;

    return 0;
}

