question.answer:
#include <iostream>  //Pre-processor directive
using namespace std; //Sets the std namespace to be used

int main(void)       //Start of the main function
{                    //Opening bracket and start of the code block for the main function
    //Declaration
    string userInput = "";  //Variable to hold user information
    int strLen = 0;         //Store the userInput length in the strLen variable
    string firstLetter = "";//Store the first letter in userInput into firstLetter
    string lastLetter = ""; //Store the last letter in userInput into lastLetter
    string newString = "";  //Store the changed userInput string with 'L' as first letter.
    int location = 0;       //Using the find method store the index location of the first instance of "um" in userInput

    //Input
    cout << "Enter a string: " << endl;
    getline(cin, userInput);

    //Processing
    newString = userInput;
    /***** ENTER CODE BELOW *****/
    strLen = userInput.size();
    firstLetter = userInput.at(0);
    lastLetter = userInput[strLen - 1];
    location = userInput.find("um");
    newString.at(0) = 'L';


    //Output
    cout << "UserInput: " << userInput << endl;
    cout << "UserInput lenght: " <<strLen << endl;
    cout << "UserInput first letter: " << firstLetter << endl;
    cout << "UserInput last letter: " << lastLetter << endl;
    cout << "UserInput with changes: " << newString << endl;
    cout << "Location of \"um\" in userInput: " << location << endl;


    return 0; //End of main function
}             //Closing bracket and end of the code block for the main function

