question.answer:
#include <iostream>  //Pre-processor directive
using namespace std; //Sets the std namespace to be used

int main(void)       //Start of the main function
{                    //Opening bracket and start of the code block for the main function
    //Declaration
    int userInput = 0;

    //Input
    cin >> userInput;

   //Start your loop here, remember to parts of a loop: Initialization, Condition, Body and Change
   // You are free to create any variables as needed.
    /***** ENTER CODE BELOW *****/
   int i = 0;       //Start with the first value you want to print
   while(i <= userInput) //Condition - use <= to ensure that the value in userInput also prints
   {
       cout << i << endl; //Print the value of i
       i++;               //Increment i by one, other option include i += 1; and i = i + 1;
   }


    return 0; //End of main function
}             //Closing bracket and end of the code block for the main function

#include <iostream>
using namespace std;
int main ( void ) {
    // set the constant CURRENT YEAR to the current year
    const int CURRENT_YEAR = 2021; 
    // --- Finally! ---

    //declare and initialize BIRTH YEAR, ANNUAL INCOME,
        // CONTRIBUTION RATE, RETIREMENT AGE, YEARS TO RETIREMENT,
        // YEAR OF RETIREMENT, TOTAL CONTRIBUTION
    int birthYear = 0;
    int annualIncome = 0;
    int contributionRate = 0;
    int retirementAge = 0;
    int yearsToRetirement = 0;
    int yearOfRetirement = 0;
    int totalContribution = 0.0;

    // get BIRTH YEAR from the user -- remember to do a prompt ...
    cout << "Enter Birth Year:" << endl;
    cin >> birthYear;
    // get ANNUAL INCOME from the user
    cout << "Enter anual income:" << endl;
    cin >> annualIncome;
    // get CONTRIBUTION RATE from the user
    cout << "Enter contribution rate (e.g., 6):" << endl;
    cin >> contributionRate;
    // get RETIREMENT AGE from the user
    cout << "Enter retirement age:" << endl;
    cin >> retirementAge;
    // calculate YEARS TO RETIREMENT from RETIREMENT AGE and BIRTH YEAR
    yearsToRetirement = retirementAge - ( CURRENT_YEAR - birthYear );
    // calculate YEAR OF RETIREMENT from BIRTH YEAR
        //and YEARS TO RETIREMENT
    yearOfRetirement = CURRENT_YEAR + yearsToRetirement;
    // calculate TOTAL CONTRIBUTION from CONTRIBUTION RATE
        //and YEARS TO RETIREMENT
    totalContribution =  annualIncome * ( contributionRate / 100.0 ) * yearsToRetirement;
    // display to the user RETIRMENT YEAR
    cout << "Retirement year: " << yearOfRetirement << endl;
    // display YEARS TO RETIREMENT to the user
    cout << "Number of years to retirement: " << yearsToRetirement << endl;
    // display ANNUAL INCOME to the user
    cout << "Annual Income: $" << annualIncome << endl;
    // display CONTRIBUTION RATE to the user
    cout << "Contribution Rate: " << contributionRate << "%" << endl;
    // display TOTAL CONTRIBUTION to the user
    cout << "Total Contribution: $" << totalContribution << endl;

}

int sqr( int n ) {
    return n * n;
}
